mapper.sql:
SELECT p.*,c.category_name,b.brand_name 
FROM own.products as p, own.category as c,own.brand_series as b 
where p.p_brandid = b.brand_id and c.category_id = p.p_categoryid and p.p_id=#{pid};

mapper.select:
SELECT p.p_id, pm.product_name as p_name, p.p_purchaprice, p.p_originprice, p.p_nowprice, p.p_brandid, p.p_categoryid, p.p_fromcompany, p.p_companyserinum, p.p_myserialnumber, p.p_fromnetaddress, p.p_storenumber, p.p_minbuyamount, p.p_freemail, p.p_jifen, p.p_hot, p.p_recommend, p.p_new, p.p_date_added, p.p_last_modified, p.p_status, p.p_beizhu, p.p_weight, p.p_freight_templet, c.category_name,b.brand_name FROM own.products as p, own.category as c,own.brand_series as b,own.productmultilanguage as pm where p.p_brandid = b.brand_id and c.category_id = p.p_categoryid and pm.product_id = p.p_id and pm.lan_id=#{lanid} and p.p_id in
#{item}
ORDER BY p_id DESC;
